import React, { Component } from 'react';
import axios from 'axios';

//import "./style.css";

const Task = props => (
    <tr>
        <td>{props.tasks.task}</td>
        <td><a href = "btn btn-success" onclick = {() => {props.deleteTask(props.tasks._id)}}>delete</a></td>
    </tr>
)

export default class Add extends Component {
    constructor(props) {
        super(props);

        this.deleteTask = this.deleteTask.bind(this);

        this.state = {tasks: [] };

    }

    componentDidMount() {
        axios.get('http://localhost:5000/tasks/')
        .then(response => {
            if(response.data.length > 0) {
                this.setState({
                    tasks: response.data.map(user => user.task),
                    task: response.data[0].task
                })
            }
        })
        .catch((error) => {
            console.log('Error: ', error);
        })
    }

      deleteTask(id) {
        axios.delete('http://localhost:5000/tasks/' + id)
        .then(res => console.log(res.data));

        this.setState({
            tasks: this.state.tasks.filter(el => el._id !== id)
        })
      }

      taskList() {
          return this.state.tasks.map(currenttask => {
              return <Task task = {currenttask} deleteIt = { this.deleteIt } key = {currenttask._id}/>;
          })
      }

    render() {
        return(
            <div>
                <table className = "table">
                    <thead className = "thead-light">
                        <tr>
                            <th>Tasks list</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.taskList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
