          })
      }*/

    render() {
        return(
            <div className = "looks">
                <h3>ADD TASKS</h3>
                <table className = "table">
                    <thead className = "thead-light">
                        <tr>
                            <th>Task</th>
                        </tr>
                    </thead>
                    <tbody>

                    </tbody>
                </table>
            </div>
        );
    }
}
