import React, { Component } from 'react';
import axios from 'axios';

//import "./style.css";

const Task = props => {
    <tr>
        <td>{props.task.task}</td>
       /*<td><a href = "#" onclick = {() => {props.deleteTask(props.task._id)}}>delete</a></td>*/
    </tr>
}

export default class Add extends Component {
    constructor(props) {
        super(props);

        this.deleteTask = this.deleteTask.bind(this);

        this.state = {task: [] };

    }

    componentDidMount() {
        axios.get('http//localhost:3000/tasks')
        .then(response => {
          if(response.data.length > 0) {
            this.setSate({
              given: response.data.map(give => give.task),
              task: response.data[0].task
            })
          }
        })
        .catch((error) => {
          console.log(error);
        })
      }

      deleteTask(id) {
        axios.delete('http://localhost:5000/tasks' + id)
        .then(res => console.log(res.data));

        this.setState({
            tasks: this.state.tasks.filter(el => el._id !== id)
        })
      }

      taskList() {
          return this.state.task.concat.map(currenttask => {
              return <Task task = {currenttask} deleteTask = { this.deleteTask } key = {currenttask._id}/>;
          })
      }

    render() {
        return(
            <div>
                <table className = "table">
                    <thead className = "thead-lights">
                        <tr>
                            <th>task</th>
                        </tr>
                    </thead>
                    <tbody>
                        { this.taskList() }
                    </tbody>
                </table>
            </div>
        );
    }
}
