import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';

import Topbar from "./topbar.js";
import Bottombar from "./bottombar.js";
//import Add from "./add.js";

const Task = props => (
  <tr>
      <td>{props.task.task}</td>
      <td>
          <button className = "btn btn-primary" onClick = {() => {props.getTask(props.task._id)}}>Show</button>
      </td>
      <td>
          <button className = "btn btn-danger" onClick = {() => {props.deleteTask(props.task._id)}}>Delete</button>
      </td>
  </tr>    
)

export default class App extends Component {
  constructor(props){
    super(props);

    this.onChangeTask = this.onChangeTask.bind(this);
    this.onChangeDescription = this.onChangeDescription.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.deleteTask = this.deleteTask.bind(this);
    this.getTask = this.getTask.bind(this);

    this.state = {
      task: '',
      description: '',
      given: [],
      tasks: [],
      descriptions: []
    };
  }

  componentDidMount() {
    axios.get('http://localhost:5000/tasks/')
    .then(response => {
        this.setState({ tasks : response.data })
    })
    .catch((error) => {
        console.log('Error: ', error);
    })
}

  onChangeTask(e) {
    this.setState({
      task: e.target.value
    });
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    });
  }

  onSubmit(e) {
    e.preventDefault();

    const here = {
      task: this.state.task,
      description: this.state.description
    }

    console.log(here)

    axios.post('http://localhost:5000/tasks/add', here)
    .then(res => console.log(res.data));

    this.setState({
      task: '',
      description: ''
    });
  }

  deleteTask(id) {
    axios.delete('http://localhost:5000/tasks/' + id)
    .then(res => console.log(res.data));

    this.setState({
        tasks: this.state.tasks.filter(el => el._id !== id)
    })
  }

  getTask(id) {
    axios.get('http://localhost:5000/tasks/' + id)
    .then(response => {

        console.log(response.data)

        this.setState ({
            descriptions : response.data.description
        })
    })
  }

  taskList() {
    return this.state.tasks.map(currenttask => {
        return <Task task = {currenttask} deleteTask = { this.deleteTask } getTask = { this.getTask } key = {currenttask._id}/>;
    })
}

  render() {

    const { descriptions } = this.state;

    return(
      <div className = "container">
      <Topbar />
      <form onSubmit = {this.onSubmit}>
        <div className = "form-group">
          <label>Task :</label>
          <input type = "text"
          required
          className = "form-control"
          value = {this.state.task}
          onChange = {this.onChangeTask}/>
        </div>
        <div className = "form-group">
          <label>Description :</label>
          <input type = "text"
          required 
          className = "form-control"
          value = {this.state.description}
          onChange = {this.onChangeDescription} />
        </div>
        <div className = "form-group">
          <input type = "submit" value = "Add" className = "btn btn-primary"/>
        </div>
      </form>
      <div>
            <table className = "table">
                <thead className = "thead-light">
                  <tr>
                      <th>Name of task</th>
                      <th>Show Description</th>
                      <th>Delete task</th>
                   </tr>
                </thead>
                <tbody>
                   this.taskList() }
                </tbody>
            </table>
            <Bottombar />
            { descriptions }
      </div>
      </div>
    )
  }
}
