import React, { Component } from 'react';
import axios from 'axios';

//import "./style.css";

export default class Bottombar extends Component {
    constructor(props) {
        super(props);

        this.deleteTask = this.deleteTask.bind(this);

        this.state = {
            now: []
        };
    }

    componentDidMount() {
        axios.get('http//localhost:5000/tasks/')
        .then(response => {
            this.setSate({ now: response.data })
        })
        .catch((error) => {
            console.log(error);
        })
      }

    render() {
        return(
            <div className = "looks">
                <h3>ADD TASKS</h3>
            </div>
        );
    }
}
